import Facebook from "../../assets/icons/facebook.svg";
import Instagram from "../../assets/icons/instagram.svg";

interface IconProps {
    /**
    * What is the icon's name?
    */
    name: string;
};

const Icon = ({
    name, 
    ...props
} : IconProps) => {
    const iconList = [
        {
            name: "facebook",
            href: "https://www.facebook.com/readchastain",
            src: Facebook,
            alt: "facebook logo"
        }
    ];

    const iconFiName = iconList.filter(selection => selection.name === name)

    const iconSelection = iconSelectionName.map(icon => {
        <a className="footer__social-media" href={icon.href} target="_blank" rel="noreferrer" {...props}>
            <img className="footer__icon" src={icon.src} alt={icon.alt} />
        </a>
    });

    return (
        <>
            {iconSelection}
        </>
    )
};

export default Icon;
