import "./Heading.scss";

interface HeadingProps {
    /**
    * What is the type of text?
    */
    type: "h1" | "h2" | "h3" | "h4";
    /**
     * What is the font family?
     */
    fontPrimary: boolean;
    /**
     * What is the content?
     */
    content: string;
}

export const Heading = ({
    type,
    fontPrimary,
    content,
    ...props
}: HeadingProps) => {
    const fontSelection = fontPrimary ? ".font__primary" : ""  

    switch (type) {
        case "h1": 
        return <h1 className={font} {...props}>{content}</h1>;
        case "h2": 
        return <h2 {...props}>{content}</h2>;
        case "h3": 
        return <h3 {...props}>{content}</h3>;
        case "h4": 
        return <h4 {...props}>{content}</h4>;
    }
};
