import Facebook from "../../assets/icons/facebook.svg";
import Instagram from "../../assets/icons/instagram.svg";

interface IconProps {
    /**
    * What is the icon's name?
    */
    name?: string;
};

const Icon = ({
    name, 
    ...props
} : IconProps) => {
    // Select icon
    const iconList = [
        {
            name: "facebook",
            href: "https://www.facebook.com/readchastain",
            imgSrc: Facebook,
            imgAlt: "facebook logo"
        }
    ]

    return (
        <a className="footer__social-media" href={iconList.href} target="_blank" rel="noreferrer">
            <img className="footer__icon" src={Facebook} alt="facebook logo" />
        </a>
    );
};

export default Icon;
