import "./NavMenu.scss";
import {NavLink} from "react-router-dom";
import {useState} from "react";

interface NavMenuProperties {
    navMenuItems: any;
}

const NavMenu = (props: NavMenuProperties) => {
    const {navMenuItems} = props;

    // Use states
    const [showMenu, setShowMenu] = useState(false);

    // Menu
    const toggleMenu = () => {
        setShowMenu(!showMenu);
    }

    // Menu items
    const individualNavItems = navMenuItems.map((navItem: { link: string; label: string; }) => {
        return (
            <NavLink
                className="nav__links__item"
                to={navItem.link}
                key={navItem.label}
            >
                {navItem.label}
            </NavLink>
        );
    });



    return (
        <div className="nav__links">

            {individualNavItems}
        </div>
    );
};

export default NavMenu;
