import { FC } from "react";
import styles from "./Text.module.scss";

interface TextProps {
  /**
   * What is the type?
   */
  type: "h1" | "h2" | "p";
  /**
   * If heading tag, is the font family primary?
   */
  primary?: boolean;
}

const Heading: FC<TextProps> = (props: any) => {
  const type = props.type;

  switch (type) {
    case "h1":
      return (
        <h1 className={selection}>
        </h1>
      );
    case "h2":
      return (
        <h2 className={selection} {...props}>
          {text}
        </h2>
      );
  }
};

export default Heading;
