import {useState} from "react";
import {NavLink} from "react-router-dom";
import "./NavMenu.scss";
import Cross from "../../../assets/images/x-circle.svg";
import List from "../../../assets/images/list.svg";

interface NavMenuProperties {
    navMenuItems: any;
}

const NavMenu = (props: NavMenuProperties) => {
    const {navMenuItems} = props;

    // Use states
    const [showMenu, setShowMenu] = useState(false);

    // Menu
    const toggleMenu = () => {
        setShowMenu(!showMenu);
    }

    // Menu Links
    const individualNavMenuItems = navMenuItems.map((navMenuItem: { link: string; label: string; }) => {
        return (
            <NavLink
                className="nav__menu__item"
                to={navMenuItem.link}
                key={navMenuItem.label}
            >
                {navMenuItem.label}
            </NavLink>
        );
    });

    return (
        <div className="nav__menu"  onClick={toggleMenu}>
            {!showMenu && <img className="nav__menu__icon" src={List} alt="list icon" />}
            {showMenu && <img className="nav__menu__icon" src={Cross} alt="cross icon" />}
            {individualNavMenuItems}
        </div>
    );
};

export default NavMenu;
