import "./Text.scss";

interface TextProps {
  /**
   * What is the type?
   */
  type: "h1" | "h2" | "p";
  /**
   * If heading tag, is the font family primary?
   */
  primary?: boolean;
  /**
   * What is the text?
   */
  text?: string;
}

const Text = ({ type, primary, text, ...props }: TextProps) => {
  const selection = primary ? "font__primary" : "font__secondary";

  switch (type) {
    case "h1":
      return (
        <h1 className={selection} {...props}>
          {text}
        </h1>
      );
    case "h2":
      return (
        <h2 className={selection} {...props}>
          {text}
        </h2>
      );
    case "p":
      return <p {...props}>{text}</p>;
    case "footer":
      return (
        <p className="footer" {...props}>
          {text}
        </p>
      );
  }
};

export default Text;
