import "./Heading.scss";

interface HeadingProps {
    /**
    * What is the type of text?
    */
    type: "h1" | "h2" | "h3" | "h4";
    /**
     * Is the he font family primary?
     */
    fontPrimary: boolean;
    /**
     * What is the title?
     */
    title: string;
}

export const Heading = ({
    type,
    fontPrimary,
    title,
    ...props
}: HeadingProps) => {
    const fontSelection = fontPrimary ? "font__primary" : "font__secondary";  

    switch (type) {
        case "h1": 
        return <h1 className={fontSelection} {...props}>{title}</h1>;
        case "h2": 
        return <h2 className={fontSelection} {...props}>{title}</h2>;
        case "h3": 
        return <h3 className={fontSelection} {...props}>{title}</h3>;
        case "h4": 
        return <h4 className={fontSelection} {...props}>{title}</h4>;
    }
};
