import Facebook from "../../assets/icons/facebook.svg";
import Instagram from "../../assets/icons/instagram.svg";

interface IconProps {
    /**
    * What is the icon's name?
    */
    name: string;
};

const Icon = ({
    name, 
    ...props
} : IconProps) => {
    switch(name) {
        // case "facebook":
        //     return (
        //     <a className="footer__social-media" href="https://www.facebook.com/readchastain" target="_blank" rel="noreferrer" {...props}>
        //         <img className="footer__icon" src={Facebook} alt="facebook logo" />
        //     </a>
        //     );
        case "facebook":
            return <img className="footer__icon" src={Facebook} alt="facebook logo" {...props} />;
    };

    switch (name) {
        case "h1": 
            return <h1 className={selection} {...props}>{text}</h1>;
        case "h2": 
            return <h2 className={selection} {...props}>{text}</h2>;
        case "h3": 
            return <h3 className={selection} {...props}>{text}</h3>;
        case "h4": 
            return <h4 className={selection} {...props}>{text}</h4>;
        case "p": 
            return <p {...props}>{text}</p>;
        case "footer":
            return <p className="footer" {...props}>{text}</p>;
    };
};

export default Icon;
