import {useState} from "react";
import "./NavBar.scss";
import NavLogo from "../atoms/NavLogo/NavLogo";
import NavMenu from "../atoms/NavMenu/NavMenu";
import List from "../../assets/images/list.svg";

const NavBar = () => {
    // Use states
    const [showMenu, setShowMenu] = useState(false);

    // Menu
    const toggleMenu = () => {
        setShowMenu(!showMenu);
    }

    const navMenuItems = [
        {
            label: "Books",
            link: "/home"
        },
        {
            label: "Illustrations",
            link: "/illustrations"
        },
        {
            label: "Blog",
            link: "/blog"
        },
        {
            label: "About",
            link: "about"
        },
        {
            label: "Contact",
            link: "contact"
        }
    ];

    return (
        <nav className="nav__bar" onClick={toggleMenu}>
            <NavLogo />
            <NavMenu navMenuItems={navMenuItems} />
        </nav>
    );
};

export default NavBar;
